create table cultura (
    idCultura bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    nome varchar(150) not null,
    CONSTRAINT cultura_pkey PRIMARY KEY (idCultura)
);
values (1, 'Cereais'), (2, 'Hortaliças'), (3, 'Cana'), (4, 'Frutas'), (5, 'Pecuária'), (6, 'Flores'), (7, 'Café');

create table area (
    idArea bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    tamanho varchar(150) not null,
    CONSTRAINT area_pkey PRIMARY KEY (idArea)
);
values (1, 'Até 50 ha'), (2, 'Até 500 ha'), (3, 'Acima de 500 ha');

create table produto (
    idProduto bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    nome varchar(150) not null,
    descricao text not null,
    idArea bigint not null references area on delete cascade,
    CONSTRAINT produto_pkey PRIMARY KEY (idProduto)
);


create table culturas_do_produto (
    idProduto bigint references produto,
    idCultura bigint references cultura
);
